# Test de is_sorted 
a = Grid(2,2, [[1,2],[4,3]])
b = Grid(3,2, [[1,2],[3,4],[5,6]])

# Résultats
'''
>>> a.is_sorted()
False
>>> b.is_sorted()
True
'''

# Test de swap
a.swap([0,1], [0,0])
b.swap([0,0], [1,1])
# Résultats
'''
>>> a.state
[[1, 2], [4, 3]]
>>> a.swap([0,1], [0,0])
>>> a.state
[[2, 1], [4, 3]]
>>> b.swap([0,0], [1,1])
'the swap is not allowed'
'''

# Test de swap_seq
a.swap_seq([[[0,1], [0,0]], [[0,0], [1,0]]])
# Résultats
'''
>>> a.state
[[2, 1], [4, 3]]
>>> a.swap_seq([[[0,1], [0,0]], [[0,0], [1,0]]])
>>> a.state
[[4, 2], [1, 3]]
'''

# Test de rpzr
b.rpzr()
# Résultats
''' Dans une autre fenêtre, on montrera en TD'''

